import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FiHeart, FiShoppingCart, FiStar, FiImage } from 'react-icons/fi';
import { useCart } from '../../context/CartContext';
import { useWishlist } from '../../context/WishlistContext';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const { addToWishlist, isInWishlist } = useWishlist();
  const [imageError, setImageError] = useState(false);

  const handleAddToCart = (e) => {
    e.preventDefault();
    addToCart(product);
  };

  const handleAddToWishlist = (e) => {
    e.preventDefault();
    addToWishlist(product);
  };

  return (
    <div className="product-card">
      <Link to={`/products/${product._id}`} className="product-link">
        <div className="product-image">
          <div 
            className="product-image-box"
            style={{ backgroundColor: product.images?.[0]?.color || '#ddd' }}
          >
            <span className="product-image-text">{product.name}</span>
          </div>
          {product.finalPrice < product.basePrice && (
            <span className="discount-badge">
              {Math.round(((product.basePrice - product.finalPrice) / product.basePrice) * 100)}% OFF
            </span>
          )}
        </div>

        <div className="product-info">
          <h3 className="product-name">{product.name}</h3>
          <p className="product-description">{product.description}</p>

          <div className="product-rating">
            <div className="stars">
              {[...Array(5)].map((_, i) => (
                <FiStar 
                  key={i} 
                  className={i < Math.floor(product.averageRating) ? 'star filled' : 'star'} 
                />
              ))}
            </div>
            <span className="rating-text">
              {product.averageRating} ({product.totalReviews} reviews)
            </span>
          </div>

          <div className="product-price">
            {product.finalPrice < product.basePrice ? (
              <>
                <span className="current-price">₹{product.finalPrice.toLocaleString('en-IN')}</span>
                <span className="original-price">₹{product.basePrice.toLocaleString('en-IN')}</span>
              </>
            ) : (
              <span className="current-price">₹{product.basePrice.toLocaleString('en-IN')}</span>
            )}
          </div>
        </div>
      </Link>

      <div className="product-actions">
        <button 
          className="btn btn-primary add-to-cart"
          onClick={handleAddToCart}
          disabled={product.stock === 0}
        >
          <FiShoppingCart />
          {product.stock === 0 ? 'Out of Stock' : 'Add to Cart'}
        </button>

        <button 
          className={`btn btn-ghost wishlist-btn ${isInWishlist(product._id) ? 'active' : ''}`}
          onClick={handleAddToWishlist}
        >
          <FiHeart />
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
