import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FiArrowRight, FiTruck, FiShield, FiHeadphones, FiRefreshCw } from 'react-icons/fi';
import { useQuery } from 'react-query';
import { productsAPI } from '../../services/api';
import ProductCard from '../../components/Products/ProductCard';
import LoadingSpinner from '../../components/UI/LoadingSpinner';

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Fetch featured products
  const { data: featuredProducts, isLoading } = useQuery(
    'featuredProducts',
    () => productsAPI.getFeaturedProducts(),
    {
      select: (data) => data.data.products || [],
      staleTime: 5 * 60 * 1000,
    }
  );

  const heroSlides = [
    {
      id: 1,
      title: "Summer Collection 2024",
      subtitle: "Discover the latest trends",
      description: "Shop our exclusive summer collection with up to 50% off on selected items",
      cta: "Shop Now"
    },
    {
      id: 2,
      title: "Tech Gadgets & Electronics",
      subtitle: "Innovation at your fingertips",
      description: "Explore cutting-edge technology and smart devices for modern living",
      cta: "Explore Tech"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroSlides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [heroSlides.length]);

  const features = [
    { icon: <FiTruck />, title: "Free Shipping", description: "Free shipping on orders over $50" },
    { icon: <FiShield />, title: "Secure Payment", description: "100% secure payment processing" },
    { icon: <FiHeadphones />, title: "24/7 Support", description: "Round-the-clock customer service" },
    { icon: <FiRefreshCw />, title: "Easy Returns", description: "30-day hassle-free returns" }
  ];

  const categories = [
    {
      name: 'Electronics',
      image: 'https://via.placeholder.com/400x300?text=Electronics',
      link: '/products?category=electronics',
      count: '50+ Products'
    },
    {
      name: 'Mens Fashion',
      image: 'https://via.placeholder.com/400x300?text=Mens+Fashion',
      link: '/products?category=fashion&subcategory=mens',
      count: '30+ Products'
    },
    {
      name: 'Womens Fashion',
      image: 'https://via.placeholder.com/400x300?text=Womens+Fashion',
      link: '/products?category=fashion&subcategory=womens',
      count: '40+ Products'
    },
    {
      name: 'Mobiles',
      image: 'https://via.placeholder.com/400x300?text=Mobiles',
      link: '/products?category=electronics&subcategory=mobiles',
      count: '25+ Products'
    }
  ];

  return (
    <div className="home-page">
      <section className="hero-section">
        <div className="hero-slider">
          {heroSlides.map((slide, index) => (
            <motion.div
              key={slide.id}
              className={`hero-slide ${index === currentSlide ? 'active' : ''}`}
              initial={{ opacity: 0 }}
              animate={{ opacity: index === currentSlide ? 1 : 0 }}
              transition={{ duration: 0.5 }}
            >
              <div className="hero-content">
                <div className="container">
                  <motion.div
                    className="hero-text"
                    initial={{ y: 50, opacity: 0 }}
                    animate={{ y: 0, opacity: 1 }}
                    transition={{ delay: 0.2, duration: 0.6 }}
                  >
                    <h2 className="hero-subtitle">{slide.subtitle}</h2>
                    <h1 className="hero-title">{slide.title}</h1>
                    <p className="hero-description">{slide.description}</p>
                    <Link to="/products" className="btn btn-primary btn-lg hero-cta">
                      {slide.cta}
                      <FiArrowRight />
                    </Link>
                  </motion.div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </section>

      <section className="features-section">
        <div className="container">
          <div className="features-grid">
            {features.map((feature, index) => (
              <motion.div
                key={index}
                className="feature-card"
                initial={{ y: 30, opacity: 0 }}
                whileInView={{ y: 0, opacity: 1 }}
                transition={{ delay: index * 0.1, duration: 0.5 }}
                viewport={{ once: true }}
              >
                <div className="feature-icon">{feature.icon}</div>
                <h3 className="feature-title">{feature.title}</h3>
                <p className="feature-description">{feature.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories-section">
        <div className="container">
          <h2 className="section-title">Shop by Category</h2>
          <div className="categories-grid">
            {categories.map((category, index) => (
              <motion.div
                key={index}
                className="category-card"
                initial={{ y: 30, opacity: 0 }}
                whileInView={{ y: 0, opacity: 1 }}
                transition={{ delay: index * 0.1, duration: 0.5 }}
                viewport={{ once: true }}
              >
                <Link to={category.link}>
                  <img src={category.image} alt={category.name} />
                  <div className="category-info">
                    <h3>{category.name}</h3>
                    <p>{category.count}</p>
                  </div>
                </Link>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Products Section */}
      <section className="featured-products-section">
        <div className="container">
          <h2 className="section-title">Featured Products</h2>
          {isLoading && <LoadingSpinner />}
          {featuredProducts && (
            <div className="products-grid">
              {featuredProducts.map((product, index) => (
                <motion.div
                  key={product._id}
                  initial={{ y: 30, opacity: 0 }}
                  whileInView={{ y: 0, opacity: 1 }}
                  transition={{ delay: index * 0.1, duration: 0.5 }}
                  viewport={{ once: true }}
                >
                  <ProductCard product={product} />
                </motion.div>
              ))}
            </div>
          )}
          <div className="text-center mt-8">
            <Link to="/products" className="btn btn-primary">
              View All Products <FiArrowRight />
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
